import React, { Component } from 'react';
import {
    Form,
    TextField,
    SelectField,
    SubmitButton
} from './../../components/FormElements';
import * as Yup from 'yup';

const formSchema1 = {
    form_inputs: [
        {
            label: "Person name",
            name: "person_name",
            type: "text",
            value: null,
            rules: "required|min:3|max:64",
            options: [],
            default_value: null,
            multiple: false,
            readonly: false,
            placeholder: "Person name",
            info: "Example value of how to fill the input"
        },
        {
            label: "Country",
            name: "country",
            type: "select",
            value: null,
            rules: "required|min:2|max:64",
            options: [
                {
                    label: "Albania",
                    value: "albania"
                },
                {
                    label: "Italy",
                    value: "italy"
                }
            ],
            default_value: "albania",
            multiple: false,
            readonly: false,
            placeholder: "select",
            info: "Example value of how to fill the input"
        }
    ]
}


class Home extends Component {
    constructor() {
        this.validator = new SimpleReactValidator();
      }

    state = {
        error: 'Ful'
    }

    handleInputChange = async (event) => {
        const value = event.target.value;
        const name = event.target.name;
        const type = event.target.type;
        await this.setState({
            [name]: value
        });
        await this.validateInputs(type, name);
    }

    validateInputs = (type, name) => {
        if (this.state[name] !== null) {
            if (this.state[name].length > 3 &&
                this.state[name].length < 64) {
                this.setState({
                    error: ''
                })
                return true;
            }
            else {
                this.setState({
                    error: `${name} should be greater than:3 char and smaller than: 64!`
                })
                return false;
            }
        }
        else {
            this.setState({
                error: `${name} is required!`
            })
            return false;
        }
    }

    handleSave = (e) => {
        e.preventDefault();
        if(this.state.error === ''){
            console.log("Submited")
        }


    }

    render() {
        return (
            <div className="container">
                <form>
                    {formSchema1.form_inputs.map(inp => (
                        <div className="form-group">
                            <label htmlFor={inp.name}>{inp.label}</label>

                            {inp.type === 'select' ?
                                <select
                                    type={inp.type}
                                    name={inp.name}
                                    className="form-control"
                                    defaultValue={inp.default_value}
                                    onChange={this.handleInputChange}
                                    id={inp.name}>
                                    <option selected>{inp.placeholder}</option>
                                    {inp.options.map(opt => <option label={opt.label} value={opt.value}></option>)}

                                </select> :
                                <input
                                    type={inp.type}
                                    name={inp.name}
                                    defaultValue={inp.default_value}
                                    placeholder={inp.placeholder}
                                    className="form-control"
                                    onChange={this.handleInputChange}
                                    id={inp.name}></input>
                            }

                        </div>
                    ))}
                    <div>
                        {this.state.error}
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={(event) => this.handleSave(event)}>Save</button>
                </form>
            </div>
        )
    }


}

export default Home;
