import React, { useState, useEffect } from 'react';
import {
    Form,
    TextField,
    SelectField,
    SubmitButton
} from './../../components/FormElements';
import * as Yup from 'yup';

const formSchema = {
    form_inputs: [{
        label: "Person name",
        name: "person_name",
        type: "text",
        value: null,
        rules: "required|min:3|max:64",
        options: [],
        default_value: null,
        multiple: false,
        readonly: false,
        placeholder: "Person name",
        info: "Example value of how to fill the input"
    },
]

}


const formSchema1 = {
    form_inputs: [
        {
            label: "Person name",
            name: "person_name",
            type: "text",
            value: null,
            rules: "required|min:3|max:64",
            options: [],
            default_value: null,
            multiple: false,
            readonly: false,
            placeholder: "Person name",
            info: "Example value of how to fill the input"
        },
        {
            label: "Country",
            name: "country",
            type: "select",
            value: null,
            rules: "required|min:2|max:64",
            options: [
                {
                    "label": "Albania",
                    "value": "albania"
                },
                {
                    "label": "Italy",
                    "value": "italy"
                }
            ],
            default_value: "albania",
            multiple: false,
            readonly: false,
            placeholder: "select",
            info: "Example value of how to fill the input"
        }
    ]
}



const Home = () => {

    const [formData, setFormData] = useState({});
    const [validationSchema, setValidationSchema] = useState({});

    const onSubmit = (values, { setSubmitting, resetForm, setStatus }) => {
        console.log(values);
        setSubmitting(false);
    }



    useEffect(() => {
        initForm(formSchema);
    }, []);


    const initForm = (formSchema) => {
        let _formData = {};
        let _validationSchema = {};


        formSchema.form_inputs.map(form => {




            console.log("formSchema", form)
            if (form.type === "text") {
                _validationSchema[form.name] = Yup.string();
            } 

            // if (formSchema[key].required) {
            //     _validationSchema[key] = _validationSchema[key].required('Required');
            // }
        })


        console.log("validation", _validationSchema)

        setFormData(_formData);
        setValidationSchema(Yup.object().shape({ ..._validationSchema }));
    }

    const getFormElement = (elementName, elementSchema) => {
        const props = {
            name: elementName,
            label: elementSchema.label,
            options: elementSchema.options
        };

        if (elementSchema.type === "text" || elementSchema.type === "email") {
            return <TextField {...props} />
        }

        if (elementSchema.type === "select") {
            return <SelectField  {...props} />
        }

    }

    return (
        <div className="App">
            <Form
                enableReinitialize
                initialValues={formData}
                validationSchema={validationSchema}
                onSubmit={onSubmit}
            >

                {Object.keys(formSchema).map((key, ind) => (
                    <div key={key}>
                        {getFormElement(key, formSchema[key])}
                    </div>
                ))}

            </Form>
        </div>
    )

}

export default Home;
