// } from './../../components/FormElements';
import SimpleReactValidator from 'simple-react-validator';
import { API_URL } from 'config';
import axios from 'axios';
import './styles.css';
// import * as Yup from 'yup';

// const formSchema1 = {
//     form_inputs: [
//         {
//             label: "Person name",
//             name: "person_name",
//             type: "text",
