import React, { Component } from 'react';
import {
    Form,
    TextField,
    SelectField,
    SubmitButton
} from './../../components/FormElements';
import * as Yup from 'yup';

const formSchema = {
    form_inputs: [{
        label: "Person name",
        name: "person_name",
        type: "text",
        value: null,
        rules: "required|min:3|max:64",
        options: [],
        default_value: null,
        multiple: false,
        readonly: false,
        placeholder: "Person name",
        info: "Example value of how to fill the input"
    },
    ]

}


const formSchema1 = {
    form_inputs: [
        {
            label: "Person name",
            name: "person_name",
            type: "text",
            value: null,
            rules: "required|min:3|max:64",
            options: [],
            default_value: null,
            multiple: false,
            readonly: false,
            placeholder: "Person name",
            info: "Example value of how to fill the input"
        },
        {
            label: "Country",
            name: "country",
            type: "select",
            value: null,
            rules: "required|min:2|max:64",
            options: [
                {
                    label: "Albania",
                    value: "albania"
                },
                {
                    label: "Italy",
                    value: "italy"
                }
            ],
            default_value: "albania",
            multiple: false,
            readonly: false,
            placeholder: "select",
            info: "Example value of how to fill the input"
        }
    ]
}


class Home extends Component {

    state = {

    }

    handleInputChange = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        this.setState({
            [name]: value
        });
    }






    render() {
        console.log("state", this.state)
        return (
            <div className="container">
                <form>
                    {formSchema1.form_inputs.map(inp => (
                        <div className="form-group">
                            <label htmlFor={inp.name}>{inp.label}</label>

                            {inp.type === 'select' ?
                                <select
                                    name={inp.name}
                                    className="form-control"
                                    defaultValue={inp.default_value}
                                    onChange={this.handleInputChange}
                                    id={inp.name}>
                                    <option selected>{inp.placeholder}</option>
                                    {inp.options.map(opt => <option label={opt.label} value={opt.value}></option>)}


                                </select> :

                                <input
                                    type={inp.type}
                                    name={inp.name}
                                    placeholder={inp.placeholder}
                                    className="form-control"
                                    onChange={this.handleInputChange}
                                    id={inp.name}></input>

                            }

                        </div>
                    ))}
                </form>




            </div>



        )
    }


}

export default Home;
