import React, { useState } from 'react';
import {
    Form,
    TextField,
    SelectField,
    SubmitButton
} from './../../components/FormElements';


const formSchema = {
    name: {
        type: "text",
        label: "Name",
        required: true
    },
    email: {
        type: "email",
        label: "Email",
        required: true
    },
    role: {
        type: "select",
        label: "Role",
        required: true,
        options: [
            {
                label: "Admin",
                value: "admin"
            },
            {
                label: "User",
                value: "user"
            }
        ]
    }
}



const Home = () => {

    const [formData, setFormData] = useState({});
    const [validationSchema, setValidationSchema] = useState({});

    const onSubmit = (values, { setSubmitting, resetForm, setStatus }) => {
        console.log(values);
        setSubmitting(false);
    }


    return (
        <div className="App">
            <Form
                enableReinitialize
                initialValues={formData}
                onSubmit={onSubmit}
            >
                <div>
                    <TextField
                        name="name"
                        label="Name"
                    />
                </div>
                <div>
                    <TextField
                        name="email"
                        label="Email"
                    />
                </div>
                <div>
                    <SelectField
                        name="role"
                        label="Role"
                        options={[
                            {
                                label: "Admin",
                                value: "admin"
                            },
                            {
                                label: "User",
                                value: "user"
                            }
                        ]}
                    />
                </div>
                <SubmitButton
                    title="Submit"
                />
            </Form>
        </div>
    )

}

export default Home;
